import styled from 'styled-components';


const Login = (props) => {
    return (
        <Container>
            <Nav>
                <a href="/">
                    <img src="/images/login-logo.svg" alt ="" />
                </a>
                <div>
                    <Join>
                        Join Now
                    </Join>

                    <SignIn>
                        Sign In
                    </SignIn>
                </div>
            </Nav>
            <Section>
                <Hero>
                    <h1>Welcome To Your Professional Community</h1>
                    <img src="/images/login-hero.svg" alt= ''></img>
                </Hero>
                <Form>
                    <Google>
                        <img src="/images/google.svg" alt=""></img>
                        Sign in with Google
                    </Google>
                </Form>
            </Section>
        </Container>
    )
};
const Container = styled.div`
padding:0px;
`;
const Nav = styled.nav`
max-width:1128px;
margin:auto;
padding:12px 0 16px;
display:flex;
align-items:center;
position:relative;
justify-content: space-between;
flex-wrap:nowrap;
& > a{
    width:135px;
    height:34px;
    @media(max-width:768px){
        padding:0 5px;
    }
};
`;
const Join = styled.a`
    font-size:17px;
    font-weight:600;
    padding:10px 12px;
    text-decoration:none;
    border-radius:6px;
    color: rgba(0,0,0,0.6);
    margin-right:12px;
    &:hover{
        background-color:rgba(0,0,0,0.08);
        color:rgba(0,0,0,0.9);
        text-decoration:none;
    }
`;
const SignIn = styled.a`
    font-size:17px;
    font-weight:600;
    padding:10px 25px;
    box-shadow:inset 0 0 0 0.5px #0a66c2;
    border-radius:24px;
    transition-duration:167ms;
    line-height:40px;
    color: #0a66c2;
    background-color:rgba(0,0,0,0);
    text-align:center;
    margin-right:12px;
    border:1.5px solid #0a66c2;
    text-decoration:none;
    &:hover{
        background-color:rgba(112,181,249,0.15);
        box-shadow:inset 0 0 0 1px #0a66c2;
        color:rgba(0,0,0,0.9);
        text-decoration:none;
    }

`;
const Section = styled.section`
    display:flex;
    align-content:start;
    min-height:700px;
    padding-bottom:138px;
    padding-top:40px;
    padding: 60px 0;
    position:relative;
    flex-wrap:wrap;
    width:100%;
    max-width:1128px;
    align-items:center;
    margin:auto;
    @media(max-width:768px){
        margin:auto;
        min-height:0px;
        justify-content: center;
    }
`;
const Hero = styled.div`
    width:100%;
    z-index:1 ;
    h1{
        padding-bottom:48px;
        width:55%;
        font-size:56px;
        color:#2977c9;
        z-index: -1;
        font-weight:200;
        line-height:70px;
        @media(max-width:768px){
            text-align:center;
            font-size:22px;
            width:100%;
            line-height:2;
        }

    }
    img{

            width:700px;
            height: 670px;
            position: absolute;
            top: -10px;
            right: -150px;
            z-index:-1;
         @media(max-width:768px){
            top:230px;
            position:initial;
            height: auto;
            min-width: 100%;
            width: 320px;
            margin-bottom: -5px;
         }
        }
`;

const Form = styled.div`
    margin-top:100px;
    width:408px;
    @media(max-width:768px){
        margin-top:20px;
    }
`;
const Google = styled.button`
    display:flex;
    justify-content:center;
    background-color:#fff;
    font-size:21px;
    align-items:center;
    height:56px;
    width:100%;
    vertical-align:middle;
    z-index:0;
    border-radius: 30px;
    border:1px solid #000000;
    color:rgba(0,0,0,0.7);
    transition-duration:167ms;
    img{
        padding-right:20px;
    }
    &:hover{
        border:2px solid #000000;
    }
    @media(max-width:768px){
        justify-content:center;
    }
`;

export default Login
